const n=(l,u)=>Math.floor(Math.random()*(u-l+1))+l;const r=()=>`rgb(${n(0,255)}, ${n(0,255)}, ${n(0,255)})`;const g=()=>{let s="";let h=0;Array(n(2,3)).fill().forEach(()=>{h=n(h,100);s=`${s} ${r()} ${h}%, `});document.body.style.backgroundImage=`linear-gradient(${n(0,360)}deg, ${s.slice(0,-2)})`};window.addEventListener("load",()=>{g()});window.addEventListener("keydown",e=>{if(e.keyCode===32)g()});